        [Fact]
        public async Task UpdateBarn_BarnNameAddressExists_ReturnsError()
        {
            var barnId = Guid.Parse("E1D40884-FF30-407A-8934-0A580D2BD57A");
            var request = new UpdateBarnRequest
            {
                BarnId = barnId,
                BarnName = "Chuồng 1 của anh A",
                Address = "Nghe An",
                Image = "data:image/png;base64,xxx",
                WorkerId = Guid.Parse("8808C9FB-825E-4BD2-FEA2-08DDAE35A557")
            };
            var existing = new Barn { Id = barnId, IsActive = true };
            _barnRepositoryMock.Setup(x => x.GetByIdAsync(barnId, default)).ReturnsAsync(existing);
            var otherBarn = new Barn { Id = Guid.NewGuid(), BarnName = "Chuồng 1 của anh A", Address = "Nghe An", IsActive = true };
            var barns = new List<Barn> { otherBarn }.AsQueryable();
            _barnRepositoryMock.Setup(x => x.GetQueryable(It.IsAny<Expression<Func<Barn, bool>>>())).Returns(barns.BuildMock());
            var result = await _barnService.UpdateBarn(request, default);
            Xunit.Assert.False(result.Succeeded);
            Xunit.Assert.Contains("đã tồn tại", result.Message);
            Xunit.Assert.Null(result.Data);
            Xunit.Assert.Contains("đã tồn tại", result.Errors[0]);
        }

        [Fact]
        public async Task UpdateBarn_WorkerNotFoundOrInactive_ReturnsError()
        {
            var barnId = Guid.Parse("E1D40884-FF30-407A-8934-0A580D2BD57A");
            var request = new UpdateBarnRequest
            {
                BarnId = barnId,
                BarnName = "Chuồng 1 của anh A",
                Address = "Nghe An",
                Image = "data:image/png;base64,xxx",
                WorkerId = Guid.NewGuid()
            };
            var existing = new Barn { Id = barnId, IsActive = true };
            _barnRepositoryMock.Setup(x => x.GetByIdAsync(barnId, default)).ReturnsAsync(existing);
            _userRepositoryMock.Setup(x => x.GetByIdAsync(request.WorkerId, default)).ReturnsAsync((User)null);
            var barnsMock = new List<Barn>().AsQueryable().BuildMock();
            _barnRepositoryMock.Setup(x => x.GetQueryable(It.IsAny<Expression<Func<Barn, bool>>>())).Returns(barnsMock);
            var result = await _barnService.UpdateBarn(request, default);
            Xunit.Assert.False(result.Succeeded);
            Xunit.Assert.Equal("Người gia công không tồn tại hoặc đã bị xóa", result.Message);
            Xunit.Assert.Null(result.Data);
            Xunit.Assert.Contains("Người gia công không tồn tại hoặc đã bị xóa", result.Errors);
        }

        [Fact]
        public async Task UpdateBarn_Success_WithNewImage()
        {
            var barnId = Guid.Parse("E1D40884-FF30-407A-8934-0A580D2BD57A");
            var workerId = Guid.Parse("8808C9FB-825E-4BD2-FEA2-08DDAE35A557");
            var request = new UpdateBarnRequest
            {
                BarnId = barnId,
                BarnName = "Chuồng 1 của anh A",
                Address = "Nghe An",
                Image = "data:image/jpeg;base64,",
                WorkerId = workerId
            };
            var existing = new Barn { Id = barnId, IsActive = true, Image = "old-image-url" };
            _barnRepositoryMock.Setup(x => x.GetByIdAsync(barnId, default)).ReturnsAsync(existing);
            var barnsMock = new List<Barn>().AsQueryable().BuildMock();
            _barnRepositoryMock.Setup(x => x.GetQueryable(It.IsAny<Expression<Func<Barn, bool>>>())).Returns(barnsMock);
            var worker = new User { Id = workerId, IsActive = true };
            _userRepositoryMock.Setup(x => x.GetByIdAsync(workerId, default)).ReturnsAsync(worker);
            _cloudinaryCloudServiceMock.Setup(x => x.DeleteImage(existing.Image, It.IsAny<CancellationToken>())).ReturnsAsync("Đã xóa thành công!");
            _cloudinaryCloudServiceMock.Setup(x => x.UploadImage(It.IsAny<string>(), "barn", It.IsAny<CancellationToken>())).ReturnsAsync("https://cloudinary.com/barn-image.jpg");
            // Act
            var result = await _barnService.UpdateBarn(request, default);
            // Assert
            Xunit.Assert.True(result.Succeeded);
            Xunit.Assert.Equal("Cập nhật chuồng trại thành công", result.Message);
            Xunit.Assert.Contains("Chuồng trại đã được cập nhật thành công. ID:", result.Data);
            Xunit.Assert.Null(result.Errors);
            _barnRepositoryMock.Verify(x => x.Update(It.IsAny<Barn>()), Times.Once());
            _barnRepositoryMock.Verify(x => x.CommitAsync(default), Times.Once());
        }

